def contains_chars(text, chars):
    if text is None or chars is None:
        return False
    text_lower = str(text).lower()
    # 若 chars 是字符串，按子串匹配；若为可迭代，则任一元素作为子串匹配
    if isinstance(chars, str):
        return chars.lower() in text_lower
    try:
        for c in chars:
            if str(c).lower() in text_lower:
                return True
        return False
    except TypeError:
        return str(chars).lower() in text_lower

def get_size(query_string):
    if not query_string:
        return 0
    params = query_string.split('%')
    if len(params) < 2:
        return 0
    value = params[1]
    return value[0:3]
def get_page_num(query_string):
    if not query_string:
        return 1
    params = query_string.split('#')
    if len(params) < 2:
        return 0
    value = params[1]
    return int(value)
    
def get_scene_id(query_string):
    if not query_string:
        return None
    params = query_string.split('&')
    for param in params:
        if '=' in param:
            parts = param.split('=', 1)
            if len(parts) == 2:
                key = parts[0]
                value = parts[1]
                if key == 'scene_id':
                    return value
    return None

# 规范化 key：去空格、统一大写、压缩多空格、替换 - _ /
def normalize_key(s):
    t = str(s or '')
    t = t.strip().upper()
    t = t.replace('-', ' ').replace('_', ' ').replace('/', ' ')
    t = ' '.join(t.split())
    return t

