ai_icon_map_normalized = None

def get_ai_icon_map_normalized():
    global ai_icon_map_normalized
    if ai_icon_map_normalized is not None:
        return ai_icon_map_normalized
    mapping = ai_button_group_icon_map()
    norm = {}
    for k, v in (mapping or {}).items():
        keyn = normalize_key(k)
        b64 = ''
        try:
            b64 = v.get('icon_base_64') or ''
        except Exception:
            b64 = ''
        norm[keyn] = b64
    ai_icon_map_normalized = norm
    return ai_icon_map_normalized

def get_ai_button_group_icon(button_group, title=None):
    # 统一返回数组；未匹配返回 []
    results = []
    if not button_group:
        return results
    key_norm = normalize_key(button_group)
    title_raw = '' if title is None else str(title)
    norm_map = get_ai_icon_map_normalized() or {}

    def get_icon(name):
        return norm_map.get(normalize_key(name)) or ''

    # 大前提1：button_group == "SCENE VIRTUAL ONOFF GANG1"
    if key_norm == normalize_key('SCENE VIRTUAL ONOFF GANG1'):
        if contains_chars(title_raw, 'dnd'):
            b64 = get_icon('SCENE VIRTUAL DND')
            return [b64] if b64 else []
        if contains_chars(title_raw, 'clean'):
            b64 = get_icon('SCENE VIRTUAL CLEAN')
            return [b64] if b64 else []
        if contains_chars(title_raw, 'master'):
            b64 = get_icon('SCENE VIRTUAL MASTER')
            return [b64] if b64 else []
        # 未命中小前提，默认按自身 key 查
        b64 = norm_map.get(key_norm) or ''
        return [b64] if b64 else []

    # 大前提2：button_group 含有 RCU DIMMING
    if contains_chars(key_norm, 'RCU DIMMING'):
        b64 = get_icon('RCU DIMMING')
        return [b64] if b64 else []

    # 大前提3：button_group 含有 OPENCLOSE
    if contains_chars(key_norm, 'OPENCLOSE'):
        # 小前提1：title 含 shade
        if contains_chars(title_raw, 'shade'):
            on_b64 = get_icon('SHADE CURTAIN ON')
            off_b64 = get_icon('SHADE CURTAIN OFF')
            results = []
            if off_b64:
                results.append(off_b64)
            if on_b64:
                results.append(on_b64)
            return results
        # 小前提2：title 含 sheer
        if contains_chars(title_raw, 'sheer'):
            on_b64 = get_icon('SHEER CURTAIN ON')
            off_b64 = get_icon('SHEER CURTAIN OFF')
            results = []
            if off_b64:
                results.append(off_b64)
            if on_b64:
                results.append(on_b64)
            return results
        return []

    # 默认：直接按规范化 key 命中
    if key_norm in norm_map:
        b64 = norm_map.get(key_norm) or ''
        return [b64] if b64 else []
    return []

# 提取标签内容，如 [en]Bedroom[/en] -> Bedroom；未命中返回原字符串
def extract_tag_content(text, tag='en'):
    if text is None:
        return ''
    s = str(text)
    s_lower = s.lower()
    open_tag = f'[{str(tag).lower()}]'
    close_tag = f'[/{str(tag).lower()}]'
    start = s_lower.find(open_tag)
    if start == -1:
        return s
    start_content = start + len(open_tag)
    end = s_lower.find(close_tag, start_content)
    if end == -1:
        return s
    return s[start_content:end]
# 安全将任意值转换为 int，失败返回 None
def to_int_or_none(value):
    try:
        if value is None:
            return None
        if isinstance(value, int):
            return value
        s = str(value).strip()
        return int(s)
    except Exception:
        return None

# 用于 subItemList 排序的键函数：无效 guest_mode_idx 置后
def sort_by_guest_mode_idx_key(item):
    v = to_int_or_none(item.get('guest_mode_idx'))
    if v is None:
        return (1, 0)
    return (0, v)

# 从function字段中提取房间名称
def extract_room_name_from_function(function_str):
    if not function_str:
        return None
    try:
        # 处理类似 '%1*1#0&&&Living Room' 的格式
        print(f"DEBUG: 处理function字段: '{function_str}'")
        # 通过&&&分隔符分割字符串
        parts = str(function_str).split('&&&')
        print(f"DEBUG: 分割后的parts: {parts}")
        if len(parts) >= 2:
            # 取&&&后面的部分作为房间名称
            room_name = parts[1].strip()
            print(f"DEBUG: 提取的房间名称: '{room_name}'")
            # 去除首尾的空格和特殊字符
            if room_name:
                return room_name
        print("DEBUG: 没有找到有效的房间名称")
        return None
    except Exception as e:
        print(f"DEBUG: 提取房间名称时发生异常: {e}")
        return None

# 从设备列表中提取房间列表：基于function字段提取房间名称，并去重
def build_room_list(device_list):
    room_list = []
    seen = set()
    print(f"DEBUG: build_room_list 开始处理 {len(device_list or [])} 个设备")

    for idx, it in enumerate(device_list or []):
        try:
            print(f"DEBUG: 处理设备 {idx+1}")
            # 从function字段提取房间名称
            function_str = it.get('function', '')
            print(f"DEBUG: 设备 {idx+1} 的function字段: '{function_str}'")

            room_name_from_function = extract_room_name_from_function(function_str)

            if room_name_from_function:
                print(f"DEBUG: 成功提取房间名称: '{room_name_from_function}'")
                profile_room = it.get('profile_room')
                print(f"DEBUG: profile_room: '{profile_room}'")

                # 提取房间的英文标签名称（如果有的话）
                room_name_en = extract_tag_content(room_name_from_function, 'en')
                out_room_name = room_name_en if room_name_en else room_name_from_function
                print(f"DEBUG: 最终房间名称: '{out_room_name}'")

                # 使用(profile_room, room_name)作为唯一键进行去重
                key = (profile_room, out_room_name)
                if key not in seen:
                    seen.add(key)
                    room_list.append({
                        'profile_room': profile_room,
                        'room_name': out_room_name
                    })
                    print(f"DEBUG: 添加房间到列表: {key}")
                else:
                    print(f"DEBUG: 房间已存在，跳过: {key}")
            else:
                print(f"DEBUG: 设备 {idx+1} 未能提取到房间名称")
        except Exception as e:
            print(f"DEBUG: 处理设备 {idx+1} 时发生异常: {e}")
            # 单个设备处理失败时跳过，继续处理其他设备
            continue

    print(f"DEBUG: build_room_list 完成，构建了 {len(room_list)} 个房间")
    return room_list

# 为 subItemList 增加 relate_room 字段，基于function字段进行匹配
def attach_relate_room(device_list, room_list):
    # 创建房间映射字典，提高查找效率
    room_map = {}
    for room in room_list:
        profile_room = room.get('profile_room')
        room_name = room.get('room_name')
        if profile_room and room_name:
            if profile_room not in room_map:
                room_map[profile_room] = []
            room_map[profile_room].append(room_name)

    # 创建房间名称到profile_room的反向映射
    room_name_to_profile = {}
    for room in room_list:
        room_name = room.get('room_name')
        profile_room = room.get('profile_room')
        if room_name and profile_room:
            room_name_to_profile[room_name] = profile_room

    for idx, it in enumerate(device_list or []):
        try:
            if idx < 4:
                it['relate_room'] = ''
                continue

            # 首先尝试从function字段提取房间名称进行匹配
            function_str = it.get('function', '')
            room_name_from_function = extract_room_name_from_function(function_str)

            if room_name_from_function:
                # 直接匹配房间名称
                if room_name_from_function in room_name_to_profile:
                    it['relate_room'] = room_name_to_profile[room_name_from_function]
                    continue

                # 尝试匹配房间名称包含关系（处理标签情况）
                for room_name, profile_room in room_name_to_profile.items():
                    if (room_name_from_function.lower() in room_name.lower() or
                        room_name.lower() in room_name_from_function.lower()):
                        it['relate_room'] = profile_room
                        break
                else:
                    # 没有找到匹配，设为空
                    it['relate_room'] = ''
            else:
                # 如果function字段没有房间信息，尝试传统的profile_room匹配
                pr = it.get('profile_room')
                if pr in room_map:
                    it['relate_room'] = pr
                else:
                    it['relate_room'] = ''

        except Exception:
            # 单个设备处理失败时设为空字符串，继续处理其他设备
            it['relate_room'] = ''
            continue

    # 组跟随：同组且 group_id 非 0，统一跟随该组首项的 relate_room
    def normalize_group_key(gid):
        if gid is None:
            return None
        # 尝试整数化
        iv = to_int_or_none(gid)
        if iv is not None:
            return str(iv) if iv > 0 else None
        s = str(gid).strip()
        if s and s != '0':
            return s
        return None

    first_group_relate = {}
    for it in (device_list or []):
        try:
            gk = normalize_group_key(it.get('group_id'))
            if not gk:
                continue
            if gk not in first_group_relate:
                first_group_relate[gk] = it.get('relate_room') or ''
        except Exception:
            continue

    if first_group_relate:
        for it in (device_list or []):
            try:
                gk = normalize_group_key(it.get('group_id'))
                if not gk:
                    continue
                if gk in first_group_relate:
                    it['relate_room'] = first_group_relate[gk]
            except Exception:
                continue

